import React, { Component } from 'react'
import styled from 'styled-components'

export default class InputBar extends Component {
    constructor(props) {
        super(props)
        this.state = {
            value: ''
        }
    }

    handleChange(event) {
        this.setState({ value: event.target.value })
    }

    handleSubmit = () => {
        // console.log(this.state.value)
        this.props.createTodo(this.state.value)
    }

    _handleKeyDown = (event) => {
        if (event.key === 'Enter') {
            this.handleSubmit()
        }
    }

    render() {
        return (
            <Wrapper>
                <ToggleAllInput id="toggle-all" />
                <ToggleLabel htmlFor="toggle-all">Mark all as complete</ToggleLabel>
                <HeaderInput value={this.state.value} onChange={(value) => this.handleChange(value)} onKeyDown={this._handleKeyDown} placeholder="What needs to be done?"></HeaderInput>
            </Wrapper>
        )
    }
}

const CheckBoxInput = styled.input.attrs(props => ({
    type: "checkbox"
}))`
    ::-webkit-input-placeholder {
        font-style: italic;
        font-weight: 300;
        color: #e6e6e6;
    }
    ::-moz-placeholder {
        font-style: italic;
        font-weight: 300;
        color: #e6e6e6;
    }
    ::input-placeholder {
        font-style: italic;
        font-weight: 300;
        color: #e6e6e6;
    }
`;
const ToggleLabel = styled.label`
    width: 60px;
    height: 34px;
    font-size: 0;
    position: absolute;
    top: 52px;
    left: -13px;
    -webkit-transform: rotate(90deg);
    transform: rotate(90deg);

    ::before {
        content: '❯';
        font-size: 22px;
        color: #e6e6e6;
        padding: 10px 27px 10px 27px;
    }
`;


const ToggleAllInput = styled(CheckBoxInput)`
    width: 1px;
    height: 1px;
    border: none; /* Mobile Safari */
    opacity: 0;
    position: absolute;
    right: 100%;
    bottom: 100%;
`;


const Input = styled.input`
    ::-webkit-input-placeholder {
        font-style: italic;
        font-weight: 300;
        color: #e6e6e6;
    }
    ::-moz-placeholder {
        font-style: italic;
        font-weight: 300;
        color: #e6e6e6;
    }
    ::input-placeholder {
        font-style: italic;
        font-weight: 300;
        color: #e6e6e6;
    }
`;

const Wrapper = styled.header``;

const HeaderInput = styled(Input)`
    position: relative;
    margin: 0;
    width: 100%;
    font-size: 24px;
    font-family: inherit;
    font-weight: inherit;
    line-height: 1.4em;
    color: inherit;
    padding: 6px;
    border: 1px solid #999;
    box-shadow: inset 0 -1px 5px 0 rgba(0, 0, 0, 0.2);
    box-sizing: border-box;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;

    padding: 16px 16px 16px 60px;
	border: none;
	background: rgba(0, 0, 0, 0.003);
	box-shadow: inset 0 -2px 1px rgba(0,0,0,0.03);
`;
