import React, { Component } from 'react'
import uuid from 'uuid'
import Todos from './components/Todos';
import styled from 'styled-components'
import ActionBar from './components/ActionBar';
import InputBar from './components/InputBar';

export default class TodoList extends Component {

    constructor(props) {
        super(props);
        const todos = [
            {
                id: 0,
                isDone: false,
                name: 'TodoItem 1'
            },
            {
                id: 1,
                isDone: true,
                name: 'TodoItem 2'
            }
        ]
        this.state = {
            todos,
            filteredTodos: todos,
            count: 0,
            toggleStatus: false
        }
    }

    createTodo = (name) => {
        this.setState({
            todos: [...this.state.todos, {id: uuid(), isDone: false, name}]
        })
    }

    updateCheckboxTodo = (todo) => {
        let { todos } = this.state
        const index = todos.map(todo => todo.id).indexOf(todo.id)
        todos[index] = todo
        this.setState({
            todos
        })
    }

    updateLabelTodo = (todo) => {
        let { todos } = this.state
        const index = todos.map(todo => todo.id).indexOf(todo.id)
        todos[index] = todo
        this.setState({
            todos
        })
    }

    getUndoneTodo = () => {
        const { todos } = this.state
        return todos.reduce((count, {isDone}) => !isDone ? count += 1: count, 0)
    }

    showAllTodo = () => {
        const { todos } = this.state
        this.setState({
            filteredTodos: todos
        })
    }

    showActiveTodo = () => {
        let { todos } = this.state
        this.setState({
            filteredTodos: todos.filter(({isDone}) => !isDone)
        })
    }

    showCompletedTodo = () => {
        let { todos } = this.state
        this.setState({
            filteredTodos: todos.filter(({isDone}) => isDone)
        })
    }

    clearCompletedTodo = () => {
        let { todos } = this.state
        this.setState({
            todos: todos.filter(({isDone}) => !isDone) 
        })
    }

    toggleAllTodo = () => {
        let { todos, toggleStatus } = this.state

        let tmp = [...todos]
        for(let i = 0; i < todos.length; i++){
            tmp[i].isDone = !toggleStatus;
        }

        // todos = todos.map(({isDone}) => isDone)
        this.setState({
            todos: [...todos]
            // toggleStatus: !toggleStatus
        })
    }

    render() {
        return (
            <Wrapper>
                <InputBar 
                createTodo={this.createTodo} 
                toggleAllTodo={this.toggleAllTodo}
                />
                <Todos 
                updateCheckboxTodo={this.updateCheckboxTodo}
                updateLabelTodo={this.updateLabelTodo}
                todos={this.state.filteredTodos} />
                <ActionBar
                allClick={this.showAllTodo}
                activeClick={this.showActiveTodo}
                completedClick={this.showCompletedTodo}
                clearCompletedClick={this.clearCompletedTodo}
                count={this.getUndoneTodo()} />
            </Wrapper>
        )
    }
}

const Wrapper = styled.section`
  background: #fff;
	margin: 60px 0 40px 0;
	position: relative;
	box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.2), 0 25px 50px 0 rgba(0, 0, 0, 0.1);
`;
