import React, { Component } from 'react'
import uuid from 'uuid'
import Todos from './components/Todos';
import styled from 'styled-components'
import ActionBar from './components/ActionBar';
import InputBar from './components/InputBar';

export default class TodoList extends Component {

    constructor(props) {
        super(props);
        this.state = {
            todos: [
                {
                    id: 0,
                    isDone: false,
                    name: 'TodoItem 1'
                },
                {
                    id: 1,
                    isDone: true,
                    name: 'TodoItem 2'
                }
            ],
            count: 0
        }
    }

    createTodo = (name) => {
        this.setState({
            todos: [...this.state.todos, {id: uuid(), isDone: false, name}]
        })
    }

    updateTodo = (todo) => {
        let { todos } = this.state
        const index = todos.map(todo => todo.id).indexOf(todo.id)
        todos[index] = todo
        this.setState({
            todos
        })
    }

    getUndoneTodo = () => {
        const { todos } = this.state
        let count = todos.reduce(({isDone}) =>{
            return isDone === false
        })
        return count;
    }

    render() {
        return (
            <Wrapper>
                <InputBar createTodo={this.createTodo} />
                <Todos updateTodo={this.updateTodo} todos={this.state.todos} />
                <ActionBar count={this.getUndoneTodo()} />
            </Wrapper>
        )
    }
}

const Wrapper = styled.section`
  background: #fff;
	margin: 60px 0 40px 0;
	position: relative;
	box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.2), 0 25px 50px 0 rgba(0, 0, 0, 0.1);
`;
