import React, { Component } from 'react'
import Todos from './components/Todos';
import styled from 'styled-components'
import ActionBar from './components/ActionBar';
import InputBar from './components/InputBar';
import TodosContext from './todos-context';

export default class TodoList extends Component {
    static contextType = TodosContext;

    constructor(props, context) {
        super(props, context);
        this.state = {
            count: 0,
            toggleStatus: false,
            todos: this.context.todos,
            filteredTodos: this.context.todos
        }
    }

    getUndoneTodo = () => {
        const { filteredTodos } = this.context
        return filteredTodos.reduce((count, {isDone}) => !isDone ? count += 1: count, 0)
    }

    toggleAllTodo = () => {
        let { todos, toggleStatus } = this.state
        this.setState({
            todos: todos.map(({id, isDone, name}) => ({id, isDone: !toggleStatus, name})),
            toggleStatus: !toggleStatus,
        }, this.filterTodos)
    }

    render() {
        return (
            <Wrapper>
                <InputBar 
                createTodo={this.context.createTodo} 
                toggleAllTodo={this.toggleAllTodo}
                />
                <Todos
                updateTodo={this.context.updateTodo}
                todos={this.context.filteredTodos} />
                <ActionBar
                clearCompletedClick={this.clearCompletedTodo}
                filter={this.context.filter}
                toggleFilter={this.context.toggleFilter}
                count={this.getUndoneTodo()} />
            </Wrapper>
        )
    }
}

const Wrapper = styled.section`
    background: #fff;
	margin: 60px 0 40px 0;
	position: relative;
	box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.2), 0 25px 50px 0 rgba(0, 0, 0, 0.1);
`;
