import React, { Component } from 'react'
import uuid from 'uuid'
import Todos from './components/Todos';
import styled from 'styled-components'
import ActionBar from './components/ActionBar';
import InputBar from './components/InputBar';
const filter = {
    0: 'showAll',
    1: 'showActive',
    2: 'showCompleted'
}
export default class TodoList extends Component {

    constructor(props) {
        super(props);
        const todos = [
            {
                id: 0,
                isDone: false,
                name: 'TodoItem 1'
            },
            {
                id: 1,
                isDone: true,
                name: 'TodoItem 2'
            }
        ]
        this.state = {
            todos,
            filteredTodos: todos,
            count: 0,
            toggleStatus: false,
            filter: filter[0]
        }
    }

    createTodo = (name) => {
        const todos = [...this.state.todos, {id: uuid(), isDone: false, name}]
        this.setState({
            todos: todos,
        }, this.filterTodos)

    }

    updateCheckboxTodo = (todo) => {
        let { todos } = this.state
        const index = todos.map(todo => todo.id).indexOf(todo.id)
        todos[index] = todo
        this.setState({
            todos,
        }, this.filterTodos)
    }

    updateLabelTodo = (todo) => {
        let { todos } = this.state
        const index = todos.map(todo => todo.id).indexOf(todo.id)
        todos[index] = todo
        this.setState({
            todos
        }, this.filterTodos)
    }

    getUndoneTodo = () => {
        const { filteredTodos } = this.state
        return filteredTodos.reduce((count, {isDone}) => !isDone ? count += 1: count, 0)
    }

    showAll = () => {
        const { todos } = this.state
        this.setState({
            filteredTodos: todos
        })
    }

    showActive = () => {
        let { todos } = this.state
        this.setState({
            filteredTodos: todos.filter(({isDone}) => !isDone)
        })
    }

    showCompleted = () => {
        let { todos } = this.state
        this.setState({
            filteredTodos: todos.filter(({isDone}) => isDone)
        })
    }

    filterTodos = () => {
        const { filter } = this.state
        this[filter]()

    }

    toggleFilter = (index) => {
        const currentFilter = filter[index]
        this.setState({
            filter: currentFilter
        }, this.filterTodos)
    }

    clearCompletedTodo = () => {
        let { todos } = this.state
        this.setState({
            todos: todos.filter(({isDone}) => !isDone) 
        }, this.filterTodos)
    }

    toggleAllTodo = () => {
        let { filteredTodos, toggleStatus } = this.state

        let tmp = [...filteredTodos]
        for(let i = 0; i < filteredTodos.length; i++){
            tmp[i].isDone = !toggleStatus;
        }

        this.setState({
            todos: filteredTodos.map(({id, isDone, name}) => {id, !toggleStatus, name}),
            toggleStatus: !toggleStatus,
        }, this.filterTodos)
    }

    render() {
        return (
            <Wrapper>
                <InputBar 
                createTodo={this.createTodo} 
                toggleAllTodo={this.toggleAllTodo}
                />
                <Todos
                updateCheckboxTodo={this.updateCheckboxTodo}
                updateLabelTodo={this.updateLabelTodo}
                todos={this.state.filteredTodos} />
                <ActionBar
                clearCompletedClick={this.clearCompletedTodo}
                filter={this.state.filter}
                toggleFilter={this.toggleFilter}
                count={this.getUndoneTodo()} />
            </Wrapper>
        )
    }
}

const Wrapper = styled.section`
  background: #fff;
	margin: 60px 0 40px 0;
	position: relative;
	box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.2), 0 25px 50px 0 rgba(0, 0, 0, 0.1);
`;
